public abstract class AbstractFiles {
  public abstract void add(AbstractFiles af);
  public abstract void remove(AbstractFiles af);
  public abstract AbstractFiles get(int  i);
  public abstract void killVirus();
}
//   
public class ImageFile extends AbstractFiles {
	private String name;
	public ImageFile(String name) {
		this.name=name;
	}
	@Override
	public void add(AbstractFiles af) {
			System.out.println("");
	}
	@Override
	public void remove(AbstractFiles af) {
		System.out.println("");
	}
	@Override
	public AbstractFiles get(int i) {
		System.out.println("");
		return null;
	}
	@Override
	public void killVirus() {
		System.out.println("--"+name+"--");
	}
}
//  
public class Folder extends AbstractFiles {
	//
	private ArrayList<AbstractFiles> list = new ArrayList<AbstractFiles>();
	private String name;
	public Folder(String name) {
		this.name=name;
	}
	@Override
	public void add(AbstractFiles af) {
		list.add(af);
		System.out.println("");
	}
	@Override
	public void remove(AbstractFiles af) {
		// TODO Auto-generated method stub
		if(list.remove(af)) {
			System.out.println("");
		}else{
			System.out.println("");
		}	
	}
	@Override
	public AbstractFiles get(int i) {
		return list.get(i);
	}
	@Override
	public void killVirus() {
		// TODO Auto-generated method stub
		System.out.println(""+name+"");
		//
		for(Object o:list) {
		((AbstractFiles)o).killVirus();
		}
	}
}
//
public class Client {
	public static void main(String[] args) {
		//
		AbstractFiles f1 = new Folder("");
		//
		AbstractFiles file1= new ImageFile("png");
		AbstractFiles file2= new ImageFile(".jpg");
		AbstractFiles file3= new ImageFile(".gif");
		f1.add(file1);
		f1.add(file2);
		f1.add(file3);
		f1.killVirus();
		file1.killVirus();
	}

```

