public class SubSystemA {
    public void startUp(){
        System.out.println("SubSystemA startUp");
    } public void shutDown(){
        System.out.println("SubSystemA shutDown");
    }
}
//B
public class SubSystemB {
    public void startUp(){
        System.out.println("SubSystemB startUp");
    } public void shutDown(){
        System.out.println("SubSystemB shutDown");
    }
}
//C
public class SubSystemC {
    public void startUp() {
        System.out.println("SubSystemC startUp");
    }

    public void shutDown() {
        System.out.println("SubSystemC shutDown");
    }
}
//
public class Facade {
    private SubSystemA subSystemA;
    private SubSystemB subSystemB;
    private SubSystemC subSystemC;

    public Facade() {
        subSystemA = new SubSystemA();
        subSystemB = new SubSystemB();
        subSystemC = new SubSystemC();
    }

    public void startUp() {
        subSystemA.startUp();
        subSystemB.startUp();
        subSystemC.startUp();
    }

    public void shutDown() {
        subSystemA.shutDown();
        subSystemB.shutDown();
        subSystemC.shutDown();
    }
}
    //
    public class test {

        public static void main(String[] args) {
            Facade facade = new Facade();
            facade.startUp();
            facade.shutDown();
        }
    }
    //
    SubSystemA startUp
    SubSystemB startUp
    SubSystemC startUp
    SubSystemA shutDown
    SubSystemB shutDown
    SubSystemC shutDown
