/***
 * 
 * update()notifyObservers()update()
 *
 */
public interface Observer {
     void update(String message);
}

/***
 * 
 * 
 */
public interface Observerable {
     void registerObserver(Observer o);

     void removeObserver(Observer o);

     void notifyObserver();
}

/**
 * 
 * update
 */
public class User implements Observer {
    private String name;
    private int age;

    public User(String name, int age) {
        this.name = name;
        this.age = age;
    }

    @Override
    public void update(String message) {
        System.out.println(age + "" + name + "" + message);
    }
}

/**
 * 
 * ObserverableObserverable
 */
public class MyServer implements Observerable {
    private List<Observer> observerList;
    private String message;

    public MyServer() {
        observerList = new ArrayList<Observer>();
    }

    @Override
    public void registerObserver(Observer o) {
        observerList.add(o);
    }

    @Override
    public void removeObserver(Observer o) {
        if (!observerList.isEmpty()) {
            observerList.remove(o);
        }
    }

    @Override
    public void notifyObserver() {
        for (int i = 0; i < observerList.size(); i++) {
            Observer observer = observerList.get(i);
            observer.update(message);
        }
    }

    public void PushMessage(String message) {
        this.message = message;
        System.out.println("" + message);
        notifyObserver();
    }
}

//
public class Test {

    public static void main(String[] args) {
        MyServer myServer = new MyServer();
        User lillusory = new User("lillusory", 22);
        User Az = new User("Az", 17);
        myServer.registerObserver(lillusory);
        myServer.registerObserver(Az);
        myServer.PushMessage("");
        System.out.println("-------------------------------");
        myServer.removeObserver(Az);//Az
        User yiqixing = new User("", 999);
        myServer.registerObserver(yiqixing);//
        myServer.PushMessage("");

    }
}
